    <Row/>
    <Row/>
    <div><Reset/></div>
    </>
  );
}

function Reset(){
  return(<>
  <button>Reset</button>
  </>)
}

function Row(){
  return (
    <div className="row">
      <Square  />
      <Square  />
      <Square  />
    </div>
  )
}

function Square(){

  //Defines a function setValue to change value
  const [value, setValue] = useState(null);

  function handleClick(){
    setValue('X');
  }

  //Value is displayed as the text in the button
  return <button className="square" onClick={handleClick}>{value}</button>
}
